import Card from '../UI/Card';
import "./Items.css";

const Item = (props) => {

    const onComplete = () => {
        const completedItem = props.obj;
        completedItem.complete = true;
        props.onComplete(completedItem);
    }

    const undoComplete = () => {
        const completedItem = props.obj;
        completedItem.complete = false;
        console.log(completedItem);
        props.undoComplete(completedItem);
    }

    return <Card className="todo-item">
        <div className="todo__description">
            <h2>{props.obj.title}</h2>
            <h2>{props.obj.dueTime.toLocaleString()}</h2>
            <h2>Priority: {props.obj.priority}</h2>
        </div>
        {!props.obj.complete && <button className="todo-item__incomplete" onClick={onComplete}></button>}
        {props.obj.complete && <button className="todo-item__complete" onClick={undoComplete}></button>}
    </Card>
};

export default Item;
